use TYPO3\FLOW3\Annotations as FLOW3;

/**
 * A quite exception handler which catches but ignores any exception.
 *
 * @FLOW3\Scope("singleton")
 */
class ProductionExceptionHandler extends \TYPO3\FLOW3\Error\AbstractExceptionHandler {

	/**
	 * Echoes an exception for the web.
	 *
	 * @param \Exception $exception The exception
	 * @return void
	 */
	protected function echoExceptionWeb(\Exception $exception) {
		if (!headers_sent()) {
			header("HTTP/1.1 500 Internal Server Error");
		}

		$referenceCode = ($exception instanceof \TYPO3\FLOW3\Exception) ? '<p>Reference code: ' . $exception->getReferenceCode() . '</p>' : '';

		echo '<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Strict//EN"
	"http://www.w3.org/TR/xhtml1/DTD/xhtml1-strict.dtd">
<html>
	<head>
		<style type="text/css">
			body {
				font-family: Helvetica, Arial, sans-serif;
				margin: 0;
			}

			h1 {
				font-size: 15px;
			}

			.TYPO3_FLOW3_WidgetLibrary_Widgets_ApplicationWindow {
				position: absolute;
				width: 100%;
				height: 100%;
				background-color: #515151;
				margin: 0;
				z-index:1000;
			}

			.TYPO3_FLOW3_WidgetLibrary_Widgets_FloatingWindow {
				width: 500px;
				height: 360px;
				background-color: none;
				background-image: url(data:image/gif;base64,);
			}

			.TYPO3_FLOW3_WidgetLibrary_Widgets_FloatingWindow .TYPO3_FLOW3_WidgetLibrary_Widgets_Window_TitleBar {
				font-size: 13px;
				position: relative;
				padding: 25px 0 0 26px;
				width: 440px;
				text-align: center;
				color: #404040;
			}

			.TYPO3_FLOW3_WidgetLibrary_Widgets_FloatingWindow .TYPO3_FLOW3_WidgetLibrary_Widgets_Window_Body {
				font-size: 14px;
				position: relative;
				padding: 30px 0 0 50px;
				width: 400px;
				text-align: left;
				color: #202020;
				line-height: 18px;
			}

			.StandardView_Package {
				width: 70px;
				float: right;
				margin: 0 0 80px 10px;
			}
		</style>

		<!--[if lte IE 7]>
		<style type="text/css">
			.TYPO3_FLOW3_WidgetLibrary_Widgets_FloatingWindow {
				background-color: #ccc;
				background-image: none;
			}
			.TYPO3_FLOW3_WidgetLibrary_Widgets_Window_TitleBar {
				background-color:#aaa;
				font-weight:bold;
			}
			.StandardView_Package {
				display:none;
			}
		</style>
		<![endif]-->

	</head>
	<body>
		<div class="TYPO3_FLOW3_WidgetLibrary_Widgets_ApplicationWindow">
			<div class="TYPO3_FLOW3_WidgetLibrary_Widgets_FloatingWindow">
				<div class="TYPO3_FLOW3_WidgetLibrary_Widgets_Window_TitleBar">FLOW3 - 500 Internal Server Error</div>
				<div class="TYPO3_FLOW3_WidgetLibrary_Widgets_Window_Body">
					<img src="data:image/gif;base64," class="StandardView_Package" />
					<h1>500 Internal Server Error</h1>
					<p>FLOW3 experienced an internal error (uncaught exception)</p>
					' . $referenceCode . '
				</div>
			</div>
		</div>
	</body>
</html>';
	}

	/**
	 * Echoes an exception for the command line.
	 *
	 * @param \Exception $exception The exception
	 * @return void
	 */
	protected function echoExceptionCli(\Exception $exception) {
		exit(1);
	}
}
?>