section.programlisting:
TYPO3.TYPO3.Content.FrontendEditor = Ext.extend(Ext.Container, {
	/**
	 * An explanation of the configuration option followed
	 * by a blank line.
	 *
	 * @cfg {Number} configTwo
	 */
	configTwo: 10
	...
}

example.programlisting:
Ext.ns("TYPO3.TYPO3.Core");
/* * * This script belongs to the FLOW3 package "TYPO3". * * * * It is free software; you can redistribute it and/or modify it under * * the terms of the GNU General Public License as published by the Free * * Software Foundation, either version 3 of the License, or (at your * * option) any later version. * * * * This script is distributed in the hope that it will be useful, but * * WITHOUT ANY WARRANTY; without even the implied warranty of MERCHAN- * * TABILITY or FITNESS FOR A PARTICULAR PURPOSE. See the GNU General * * Public License for more details. * * * * You should have received a copy of the GNU General Public License * * along with the script. * * If not, see http://www.gnu.org/licenses/gpl.html * * * * The TYPO3 project - inspiring people to share! * * */
/** * @class TYPO3.TYPO3.Core.Application * * The main entry point which controls the lifecycle of the application. * * @namespace TYPO3.TYPO3.Core * @extends Ext.util.Observable * @singleton */ TYPO3.TYPO3.Core.Application = Ext.apply(new Ext.util.Observable, { // here comes the class contents });

callout.para:
You should add a @singleton annotation to the
            class doc comment after the @namespace and
            @extends annotation

listitem.para:
@namespace
          Name.Of.Namespace
          (required)

@extends
          Name.Of.BaseClass
          (required)

@param {type}
          nameOfParameter description
          of parameter for every parameter of the
          constructor

example.programlisting:
...
TYPO3.TYPO3.Core.Application = Ext.apply(new Ext.util.Observable, { // this is just an example class definition
	... propery definitions ...
	/**
	 * This is a method declaration; and the
	 * explanatory text is followed by a newline.
	 *
	 * @param {String} param1 Parameter name
	 * @param {String} param2 (Optional) Optional parameter
	 * @return {Boolean} Return value
	 */
	aPublicMethod: function(param1, param2) {
		return true;
	},

	/**
	 * this is a private method of this class,
	 * the private anotation marks them an prevent that they
	 * are listed in the api doc. As they are private, they
	 * have to start with an underscore as well.
	 *
	 * @return {void}
	 * @private
	 */
	_sampleMethod: function() {
	}
}
...

listitem.programlisting:
function() {
	var myVariable1, myVariable2, someText;
	// now, use myVariable1, ....
}

// DO:
function() {
	var myVariable1, myVariable2;
	...
}
// DO:
function() {
	var myVariable1 = {}, myVariable2 = [], myVariable3;
	...
}
// DON'T
function() {
	var variable1 = 'Hello',
			variable2 = variable1 + ' World';
	...
}

function() {
	var foo;
		// Explain what we are doing here.
	foo = '123';
}

if (myExpression) {
	// if part
} else {
	// Else Part
}

// for simple parameters:
new Ext.blah(options, scope, foo);
object.myMethod(foo, bar, baz);

// when the method takes a single parameter of type object as argument, and this object is specified directly in place:
new Ext.Panel({
	a: 'b',
	c: 'd'
});

// when the method takes more parameters, and one is a configuration object which is specified in place:
new Ext.blah(
	{
		foo: 'bar'
	},
	scope,
	options
);

section.programlisting:
// scan one file or multi-files
jslint file.js
jslint file-1.js file-2.js

//scan one directory or multi-directory
jslint directory
jslint directory-1 directory-2

//scan current directory
jslint .

TYPO3.TYPO3.Content.DummyStore = Ext.extend(Ext.data.Store, {

	constructor: function(cfg) {
		cfg = cfg || {};
		var config = Ext.apply(
			{
				autoLoad: true
			},
			cfg
		);

		TYPO3.TYPO3.Content.DummyStore.superclass.constructor.call(
			this,
			config
		);
	}
});
Ext.reg('TYPO3.TYPO3.Content.DummyStore', TYPO3.TYPO3.Content.DummyStore);
